mapper.update:
update
	   board
   set
   	   board_title = #{boardTitle} 
  	  ,board_content = #{boardContent}
 where
 	   board_no = #{boardNo}

update
	   art_board
   set
   	   FILE_TYPE = #{fileType}
   	  ,DPI = #{dpi}
   	  ,DEFAULT_SIZE = #{defaultSize}
   	  ,MODIFY_COUNT = #{modifyCount}
   	  ,WORKDAY = #{workday}
   	  ,CATEGORY = #{category}
   	  ,PRICE = #{price}
 where
 	   board_no = #{boardNo}

update
       board_image
   set
       origin_name = #{ originName }
      ,modify_name = #{ modifyName }
      ,upload_date = #{ uploadDate }
      ,file_path = #{ filePath }
 where
       board_img_no = #{ boardImgNo }

mapper.insert:
insert
  into
  	   board_image
  	   (
  	   BOARD_IMG_NO
  	  ,BOARD_NO
  	  ,ORIGIN_NAME
  	  ,MODIFY_NAME
  	  ,UPLOAD_DATE
  	  ,FILE_LEVEL
  	  ,FILE_PATH
  	   )
values
	   (
       seq_boardimg_no.nextval
      ,#{boardNo}
      ,#{originName}
      ,#{modifyName}
      ,sysdate
      ,#{fileLevel}
      ,#{filePath}
	   )

mapper.update:
update
       board
   set
       board_content = #{ boardContent }
 where
       board_no = #{ boardNo }
