mapper.select:
SELECT * FROM( SELECT ROWNUM RNUM ,A.* FROM(SELECT BOARD.BOARD_NO ,BOARD.BOARD_TITLE ,MEMBER.NICKNAME ,BOARD_IMAGE.MODIFY_NAME ,BOARD_IMAGE.UPLOAD_DATE ,FILE_PATH ,BOARD_COUNT FROM BOARD LEFT JOIN MEMBER ON(BOARD.MEMBER_NO = MEMBER.MEMBER_NO) LEFT JOIN
BOARD_IMAGE ON(BOARD.BOARD_NO = BOARD_IMAGE.BOARD_NO) LEFT JOIN MEMBER_IMAGE ON(BOARD.MEMBER_NO = MEMBER_IMAGE.MEMBER_NO) LEFT JOIN VOTE ON(BOARD.BOARD_NO = VOTE.BOARD_NO) LEFT JOIN EMOTICON ON(BOARD.BOARD_NO = EMOTICON.BOARD_NO)
WHERE BOARD_IMAGE.FILE_LEVEL = 1 AND EMOTICON.PRICE_STATUS = 0 AND WINNER = 0 ORDER BY UPLOAD_DATE DESC)A) WHERE RNUM BETWEEN #{ startNumber } AND #{ endNumber }

