mapper.update:
 UPDATE 
 		board
  SET 
      board_title = #{boardTitle}
      , board_content = #{boardContent}
      , create_date = SYSDATE
WHERE board_no = #{boardNo}

update
      goods
  set
      price = #{price}
      , draft = #{draft}
      , modify_count = #{modifyCount}
      , WORKDAY = #{workday}
      , add_draft = #{addDraft}
      , add_modify =  #{addModify}
      , goods_category = #{goodsCategory}
where 
      board_no = #{boardNo}
AND     member_no =  #{memberNo}

  update board_image
    set
        origin_name = #{originName}
        , modify_name = #{modifyName}
        , upload_date = SYSDATE
        , file_level = #{fileLevel}
        , file_path = #{filePath}
  where 
		board_no = #{boardNo}

mapper.delete:
  DELETE
	FROM heart
	WHERE 
		 board_no = #{boardNo}
	and     
		 member_no = #{memberNo}
