mapper.delete:
delete
	   secretcode
 where
 	   who = #{who}
   and
   	   secret = #{secret}

mapper.insert:
insert
  into
       member
       (
       member_no
      ,member_grade
      ,member_id
      ,member_pwd
      ,member_name
      ,nickname
      ,phone
      ,email
      ,email_agree
      ,status
      ,enroll_date
       )
values
	   (
	   seq_member_no.nextval
	  ,#{memberGrade}
	  ,#{memberId}
	  ,#{memberPwd}
	  ,#{memberName}
	  ,#{nickName}
	  ,#{phone}
	  ,#{email}
	  ,#{emailAgree}
	  ,default
	  ,default			   
	   )

insert
  into
  	   member_image
  	   (
  	   member_img_no
  	  ,member_no
  	  ,member_origin_name
  	  ,member_modify_name
  	  ,member_upload_date
  	  ,member_file_path
  	   )
values
	   (
	   seq_memberimg_no.nextval
	  ,#{memberNo}
	  ,#{memberOriginName}
	  ,#{memberOriginName}
	  ,sysdate
	  ,#{memberFilePath}
	   )
